import { json, useParams, us } from "react-router-dom";

function EventDetailPage() {
  const params = useParams();
  return (
    <>
      <h1>Event Detail Page</h1>
      <p>{params.eventId}</p>
    </>
  );
}

export default EventDetailPage;

export async function loader({ param}) {
  const id = param.eventId;

  const response = await fetch("http://localhost:8080/events/" + id);

  if (!response.ok) {
    return json({ message: "Could not fetch the selected event details" }, {
      status: 500
    })
  }

  return response;
}
