import { json, useRouterLoaderData } from "react-router-dom";

import EventsList from "../components/EventsList";

function EventsPage() {
  const data = useRouterLoaderData("event-detail");
  const events = data.events;

  return (
      <EventsList events={events} />
  );
}

export default EventsPage;

export async function loader() {
  const response = await fetch("http://localhost:8080/events");

  if (!response.ok) {
    // throw new Response(JSON.stringify({ message: "Could not fetch events data" }), {
    //     status: 500
    // });
    throw json(
      { message: "Could not fetch events data" },
      {
        status: 500,
      }
    );
  } else {
    return response;
  }
}
