import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useSelector } from "react-redux";
import { useEffect, Fragment } from "react";
import { uiActions } from "./store/ui-slice";
import { useDispatch } from "react-redux";
import Notification from "./components/UI/Notification";

function App() {
  const isVisible = useSelector((state) => state.ui.cartIsVisible);
  const cart = useSelector(state => state.cart);
  const notification = useSelector(state => state.ui.notification);
  const dispatch = useDispatch();

  useEffect(() => {
    const sendCartData = async () => {
      dispatch(uiActions.showNotification({
        status: "pending",
        title: "Sending data...",
        message: "Is sending cart data..."
      }));

      const response = fetch("https://reduxcart-dbb74-default-rtdb.firebaseio.com/cart.json", {
        method: "PUT",
        body: JSON.stringify(cart)
      });

      if (!response.ok) {
        throw new Error("Sending cart data failed!");
      }

      dispatch(uiActions.showNotification({
        status: "success",
        title: "Sending data successfully",
        message: "Successfully sending cart data!"
      }))
    }

    sendCartData.catch((error) => {
      dispatch(uiActions.showNotification({
        status: "error",
        title: "Sending data failed",
        message: "Sending cart data failed!"
      }))
    })
  }, [cart]);

  return (
    <Fragment>
      {notification && <Notification status={notification.status} title={notification.title} message={ notification.message} />}
      <Layout>
        {isVisible && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
